import matplotlib.pyplot as plt
import numpy as np


fig = plt.figure(figsize=(12.8, 9.6))
plt.title("20 Robots Training (DDPG)", color='red', fontsize='large', fontweight='bold')
plt.xlim(0, len(scores)+2)
plt.axis('off')

ax1 = fig.add_subplot(311)
ax1.plot(np.arange(1, len(scores) + 1), scores, label='Score')
ax1.plot(np.arange(1, len(avg_scores) + 1), avg_scores, 'g-.', label='Average Score', linewidth=0.8)
ax1.hlines(30, 1, len(scores)+1, colors='r', linestyles='dashed')
ax1.set_ylabel('Score')
ax1.set_xlabel('Episode #')
ax1.legend()

ax2 = fig.add_subplot(312)
ax2.plot(np.arange(1, len(actor_losses) + 1), actor_losses)
ax2.set_ylabel('Actor Loss')
ax2.set_xlabel('Episode #')

ax3 = fig.add_subplot(313)
ax3.plot(np.arange(1, len(critic_losses) + 1), critic_losses)
ax3.set_ylabel('Critic Loss')
ax3.set_xlabel('Episode #')

plt.savefig('Continuous_Control_DDPG_20_robots.png')
