import matplotlib.pyplot as plt
import numpy as np


fig = plt.figure(figsize=(12.8, 9.6))
plt.title("One Robot Training (DDPG)", color='red', fontsize='large', fontweight='bold')
plt.xlim(0, len(scores)+2)
plt.axis('off')

